import React, {useState} from 'react'
// import Form from 'react-bootstrap/Form';
import {MdEmail} from 'react-icons/md';
import {CgDetailsMore} from 'react-icons/cg';
function Contact(){
  const [data, setData] = useState({email:'',details:'',});
  let name,value;
  const getData =(e)=>{
    name = e.target.name;
    value = e.target.value;
    setData({...data,[name]:value});
  }
  const postData = async(event)=>{
    const {email,details}= data;
    event.preventDefault();
    const res = await fetch('https://storage-885b2-default-rtdb.firebaseio.com/ContactData.json',{
      method:"POST",
      headers:{
      "Content-Type":"application/json",
      },
      body:JSON.stringify({
        email,
        details,
      })
    })
    if(res){
      setData({ email:'',details:'',})
    }


  }
  return(<>
  <div className='title'>
        <h2>Contact Us</h2>
        <div className='underline'></div>
      </div>
  <div className='container-form'>
 
    <form className='review' method='POST'>
    <div className='only-height'>
      <div>
       <MdEmail className='icon-email'/> <h3>E-mail</h3>
      <input  
      onChange={getData} type='email' name='email'  autoComplete='off' value ={data.email} className='email-input delete-btn'/>
      
      </div>
      <br/>
      <div>
       <CgDetailsMore  className='icon-details'/> <h3>Details</h3>
      <input type='text' name='details' autoComplete='off' value ={data.details}
      onChange ={getData} className='details-input delete-btn'/>
      </div>
      <br/>
      <button className='btn' onClick={postData}>Send</button>
      </div>
    </form>

  </div>
  
  </>)
}
export default Contact;
